const response = msg.payload;
const responseType = response.type;
const responseId = response.id;

const vm1 = flow.get("vm1");
const vm2 = flow.get("vm2");
const vm3 = flow.get("vm3");
const vm4 = flow.get("vm4");
const vm5 = flow.get("vm5");
const vm6 = flow.get("vm6");
const vm7 = flow.get("vm7");
const vm8 = flow.get("vm8");
const vm9 = flow.get("vm9");
const vm10 = flow.get("vm10");

if (responseType === "product") {
    msg.topic = "product";
    msg.payload = replaceVMWithLocation(response.locations, vm1, vm2, vm3, vm4, vm5, vm6, vm7, vm8, vm9, vm10);
    return msg;
}
else if (responseType === "resupply") {
    msg.topic = "resupply";
    msg.payload = replaceVMIdsWithLocations(response.schedule, vm1, vm2, vm3, vm4, vm5, vm6, vm7, vm8, vm9, vm10);
    return msg;
}


function replaceVMWithLocation(vmData, ...vms) {
    const result = {};
    for (const vm of vms) {
        if (vmData.hasOwnProperty(vm.id)) {
            const location = vm.location; // Use eval() to dynamically access the location property
            result[location] = { ...vmData[vm.id] };
        }
    }
    return result;
}

function replaceVMIdsWithLocations(schedule, ...vms) {
    // Iterate over each date in the schedule
    for (const date in schedule) {
        if (Array.isArray(schedule[date])) {
            // Replace VM IDs with location names for each date
            schedule[date].forEach((vmId, index) => {
                const vm = vms.find(vm => vm.id === vmId);
                schedule[date][index] = vm ? vm.location : vmId;
            });
        }
    }

    return schedule;
}

