function calculateTotalQuantityPerProduct(masterObject) {
    let result = {};

    // Iterate over each vending machine
    for (let vmId in masterObject) {
        if (masterObject.hasOwnProperty(vmId)) {
            let vendingMachine = masterObject[vmId];

            // Iterate over each product in the vending machine
            for (let productId in vendingMachine) {
                if (vendingMachine.hasOwnProperty(productId)) {
                    let product = vendingMachine[productId];

                    // Initialize the total quantity for the product if not done yet
                    if (!result[productId]) {
                        result[productId] = 0;
                    }

                    // Add the quantity of the product at the current vending machine
                    result[productId] += product.quantity;
                }
            }
        }
    }

    return result;
}

