function updatePrice(p, newPrice) {
    // Loop through each vending machine in masterObject
    for (const vmId in masterObject) {
        if (masterObject.hasOwnProperty(vmId)) {
            const vm = masterObject[vmId];

            // Check if the product exists in the current vending machine
            if (vm.hasOwnProperty(p.id)) {
                // Update the price for the specified product
                vm[p.id].price = newPrice;
            }
        }
    }
    if (masterObjectAlt.hasOwnProperty(p.id)) {
        const product = masterObjectAlt[p.id];
        for (const vmId in product) {
            // Check if the product exists in the current vending machine
            if (product.hasOwnProperty(vmId)) {
                const vm = product[vmId];
                // Update the price for the specified product
                vm[p.id].price = newPrice;
            }
        }
    }
}

