        }
}
else if (requstType === "product"){
    msg.topic = request.id;

    if (isEveryVMQuantityZero(masterObjectAlt, request.requestId)){
        if (isArrayEmpty(flow.get("scheduleList_" + request.requestId))){
            msg.payload = {
                type: "resupply",
                id: request.requestId,
                schedule: "no"
            }
        }
        else{
            msg.payload = {
                type: "resupply",
                id: request.requestId,
                schedule: flow.get("scheduleList_" + request.requestId)
            }
        }

    }
    else{
        msg.payload = {
            type: "product",
            id: request.requestId,
            locations: masterObjectAlt[request.requestId]
        }
    }
}

return msg;

function isEveryVMQuantityZero(masterObjectAlt, productId) {
    // Check if the product exists in masterObjectAlt
    if (masterObjectAlt.hasOwnProperty(productId)) {
        const productVMs = masterObjectAlt[productId];

        // Check if every VM for the product has quantity zero
        return Object.values(productVMs).every(vm => vm.quantity === 0);
    }

    // Return false if the product does not exist in masterObjectAlt
    return false;
}

function isArrayEmpty(arr) {
    return arr.length === 0;
}

const object = msg.payload;
