const cart = object.products;

const masterObject = flow.get("masterObject");
const masterObjectAlt = flow.get("masterObjectAlt");


if (masterObject.hasOwnProperty(vmId)){
    const vm = masterObject[vmId];
    for (const pId in cart) {
        if (cart.hasOwnProperty(pId) && vm.hasOwnProperty(pId)){
            const p = vm[pId];
            p.quantity -= cart[pId];
            p.quantity = Math.max(0, p.quantity);
        }
    }
}
for (const pId in cart) {
    if (masterObjectAlt.hasOwnProperty(pId)) {
        const p = masterObjectAlt[pId];
        if (cart.hasOwnProperty(pId) && p.hasOwnProperty(vmId)) {
            const vm = p[vmId];
            vm.quantity -= cart[pId];
            vm.quantity = Math.max(0, vm.quantity)
        }

    }
}

return msg;

